import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useParams} from 'react-router-dom'
import {useHistory} from 'react-router-dom'

const EditProduct = ({products, onEdit}) => {
    let {id} = useParams();
    const product = products.find(product => product.id === id);

    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
    const onHandleChange = (e) => {
        const {name, value} = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value
        })
    }
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onEdit(valueInput);
        history.push("/products");
    }
    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Add Product</h1>
            <form onSubmit = {onHandleSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" value={product.name} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Image</label>
                    <input type="text" className="form-control" name="image" value={product.name} onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" name="price" value={product.name} onChange={onHandleChange}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
