import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useHistory} from 'react-router-dom'
import { useForm } from "react-hook-form";

const AddProduct = ({onAdd}) => {
    const { handleSubmit, register, errors } = useForm();
    let history = useHistory();
    // const [valueInput, setValueInput] = useState({});
    // const onHandleChange = (e) => {
    //     const {name, value} = e.target;
    //     setValueInput({
    //         ...valueInput,
    //         [name]: e.target.value
    //     })
    // }
    const onHandleSubmit = (data) => {
        onAdd(data);
        history.push("/admin/products");
    }
    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Add Product</h1>
            <form onSubmit={handleSubmit(onHandleSubmit)}>
                <div className="form-group">
                    <label>Name</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="name"
                        ref={register({
                           required:true,
                           minLength:10,
                           maxLength:20, 
                        })}
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.name && errors.name.type === "required" &&  <span>Tên sản phẩm không được để trống</span>}
                        {errors.name && errors.name.type === "minLength" && <span>Min Length 10</span>}
                        {errors.name && errors.name.type === "maxLength" && <span>Max Length 20</span>}
                    </small>
                </div>
                <div className="form-group">
                    <label>Image</label>
                    <input 
                        type="file" 
                        className="form" 
                        name="image"
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.image && errors.image.type === "required" && <span>Ảnh không được để trống</span>}   
                    </small>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="price"
                        ref={register({
                            required:true, 
                            pattern: {
                                value: /\d/
                            }
                        })}
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.price && errors.price.type === "required" && <span>Giá không được để trống</span>}
                        {errors.price && errors.price.type === "pattern" && <span>Invalid number</span>}
                    </small>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
