import React, { useState } from 'react';
import dataFake from './dataFake';
import Routers from './routers'

function App() {
  const [products, setProducts] = useState(dataFake);

  const onHandleAddProduct = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }

  const onHandleEditProduct = () => {

  }

  const onHandleRemoveProduct = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  return (
    <div className="App">
      <Routers products={products} onRemoveProduct={onHandleRemoveProduct} onAddProduct={onHandleAddProduct} onEditProduct={onHandleEditProduct}/>
    </div>
  )

}
export default App;
