import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useHistory} from 'react-router-dom'

const AddProduct = ({onAdd}) => {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
    const onHandleChange = (e) => {
        const {name, value} = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value
        })
    }
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onAdd(valueInput);
        history.push("/products");
    }
    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Add Product</h1>
            <form onSubmit = {onHandleSubmit}>
                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" name="name" onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Image</label>
                    <input type="text" className="form-control" name="image" onChange={onHandleChange}/>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input type="text" className="form-control" name="price" onChange={onHandleChange}/>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
