import React, {useState , useEffect} from 'react'
import PropTypes from 'prop-types'
import {useParams, useHistory} from 'react-router-dom'
import apiProductRequest from '../../../../api/productApi'
import { useForm } from "react-hook-form";

const EditProduct = ({products, onUpdate}) => {
    const { handleSubmit, register, errors } = useForm();
    let {id} = useParams();
    let history = useHistory();
    const [currentProduct, setCurrentProduct] = useState({});

    const showProduct = async () => {
        const {data} = await apiProductRequest.get(id);
        setCurrentProduct(data);
    }

    useEffect( () => {
        showProduct();
    },[])

    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    const onHandleSubmit = (e) => {
        onUpdate(id,currentProduct);
        history.push('/admin/products');
    }
    return (
        <div>
            <h1 class="h3 mb-0 text-gray-800">Edit Product</h1>
            <form onSubmit = {handleSubmit(onHandleSubmit)}>
                <div className="form-group">
                    <label>Name</label>
                    <input 
                    type="text" 
                    className="form-control" 
                    name="name" 
                    value={currentProduct.name} 
                    onChange={onHandleChange}
                    ref={register({
                        required:true,
                        minLength:5,
                        maxLength:15, 
                     })}
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.name && errors.name.type === "required" &&  <span>Tên sản phẩm không được để trống</span>}
                        {errors.name && errors.name.type === "minLength" && <span>Tối thiểu 5 ký tự</span>}
                        {errors.name && errors.name.type === "maxLength" && <span>Tối đa 15 ký tự</span>}
                    </small>
                </div>
                <div className="form-group">
                    <label>Image</label><br />
                    <img src={currentProduct.image} width="100px"/>
                    <input 
                    type="text" 
                    className="form-control" 
                    name="image" 
                    value={currentProduct.image} 
                    onChange={onHandleChange}
                    ref={register({
                        required:true,
                        pattern: {
                            value: /\.(gif|jpg|jpeg|tiff|png|webp|bmp)$/i
                        }
                    })}
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.image && errors.image.type === "required" && <span>Ảnh không được để trống</span>} 
                        {errors.image && errors.image.type === "pattern" && <span>Sai định dạng ảnh</span>}   
                    </small>
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input 
                    type="text" 
                    className="form-control" 
                    name="price" 
                    value={currentProduct.price} 
                    onChange={onHandleChange}
                    ref={register({
                        required:true, 
                        pattern: {
                            value: /\d/
                        }
                    })}
                    />
                    <small id="nameHelp" className="form-text text-danger">
                        {errors.price && errors.price.type === "required" && <span>Giá không được để trống</span>}
                        {errors.price && errors.price.type === "pattern" && <span>Giá không thể nhập chữ</span>}
                    </small>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
